package programmingelements;

public class Km_to_miles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double kms = 10.8;
		double miles = 1.6;
		
		double km_to_miles = 10.8 * miles;
		
		System.out.println("The distance " + kms + " in miles is " + km_to_miles);
	}

}
